                <li
                    style={currentTab === "contact" ? listactive : list}
                    className={currentTab === "contact" ? "active-tab list-group-item" : "list-group-item"}
                    onClick={() => setCurrentTab("contact")}>
                    contact
                </li>
            </ul>
        </nav >
